<?php

class OSF_Import {
    private $config, $path_rev, $homepage, $blogpage, $settings, $templates;

    public function __construct() {
        if (is_admin()) {
            $this->path_rev = trailingslashit(wp_upload_dir()['basedir']) . 'opal_rev_sliders_import/';
            add_action('after_setup_theme', array($this, 'init'));
        }
    }

    public function init() {
        $this->init_hooks();
        add_filter('pt-ocdi/disable_pt_branding', '__return_true');
    }

    public function init_hooks() {
        add_action('osf_before_import_customizer', array($this, 'reset_theme_mods'));
        if (get_option('otf-oneclick-first-import', 'yes') === 'yes') {
            add_filter('pt-ocdi/import_files', array($this, 'import_file_base'));
            add_action('pt-ocdi/after_import', array($this, 'after_import_setup'));
            add_action('pt-ocdi/after_import', array($this, 'after_import_setup_base'), 20);
        } else {
            add_filter('pt-ocdi/import_files', array($this, 'import_files'));
            add_action('pt-ocdi/after_import', array($this, 'after_import_setup'));
            add_filter('pt-ocdi/enable_grid_layout_import_popup_confirmation', '__return_false');
            add_filter('otf-ocdi/reload_page', '__return_false');
        }
    }

    public function import_file_base() {
        $this->init_data();
        $imports   = array();
        $import    = array(
            'import_file_name'  => 'Home 1',
            'local_import_file' => trailingslashit(XSTREET_CORE_PLUGIN_DIR) . 'dummy-data/content.xml',
            'import_notice'     => 'Basic import includes default version from our demo and a few products, blog posts and portfolio projects. It is a required minimum to see how our theme built and to be able to import additional versions or pages.',
            'slug'              => '1',
            'customizer'        => esc_url('http://source.wpopal.com/striz/dummy_data/striz/customizer.json'),
            'elementor'         => esc_url('http://source.wpopal.com/striz/dummy_data/striz/elementor.json'),
            'settings'          => esc_url('http://source.wpopal.com/striz/dummy_data/striz/settings.json'),

        );
        $imports[] = $import;

        return $imports;
    }

    public function import_files() {
        $this->init_data();
        $imports = array();
        foreach ($this->config as $key => $item) {
            $import = array(
                'import_file_name'         => $item['name'],
                'import_preview_image_url' => $item['screenshot'],
                'slug'                     => $key,
                'settings'                 => esc_url('http://source.wpopal.com/striz/dummy_data/striz/settings.json'),
            );
            if (isset($item['xml'])) {
                $import['import_file_url'] = $item['xml'];
            }

            $imports[] = $import;
        }
        return $imports;
    }

    private function init_data() {

        $this->config   = $this->get_data_config();
        $this->blogpage = osf_get_page_by_title('Blog');
    }

    public function after_import_setup_base($selected_import) {
        update_option('show_on_front', 'page');
        update_option('page_on_front', (($this->homepage instanceof WP_Post) ? $this->homepage->ID : 0));
        update_option('page_for_posts', (($this->blogpage instanceof WP_Post) ? $this->blogpage->ID : 0));
        update_option('otf-oneclick-first-import', 'no');
        $check_oneclick  = get_option('striz_check_oneclick', []);


        // Setup Elementor
        $this->fixelementor();
        $elementor = $this->get_remote_json($selected_import['elementor'], true);
        if (osf_is_elementor_activated()) {

            foreach ($elementor as $key => $value) {
                update_option($key, $value);
            }
        }

        if(!isset($check_oneclick['menu-item'])){
            $this->update_nav_menu_item();
            $check_oneclick['menu-item'] = true;
        }

        $this->setup_elementor_global();

        // Breadcrumb
        $this->settings = $this->get_remote_json($selected_import['settings'], true);
        update_option('bcn_options', $this->settings['breadcrumb']);

        // Mailchimp
        $mailchimp = osf_get_page_by_title('Opal MailChimp', OBJECT, 'mc4wp-form');
        if ($mailchimp) {
            update_option('mc4wp_default_form_id', $mailchimp->ID);
        }

        $this->set_placehoder();

        // Update Logo
        set_theme_mod('custom_logo', 6065);
    }

    private function set_placehoder() {
        $attach_id = media_sideload_image(trailingslashit(XSTREET_CORE_PLUGIN_URL) . 'assets/images/placeholder.jpg', 0, null, 'id');
        $params    = array(
            'posts_per_page' => -1,
            'post_type'      => ['product', 'post'],
            'meta_query'     => [
                [
                    'key'   => '_opal_demo',
                    'value' => 'demo',
                ]
            ]
        );
        $query     = new WP_Query($params);
        while ($query->have_posts()) : $query->the_post();
            set_post_thumbnail(get_the_ID(), $attach_id);
        endwhile;
        set_post_thumbnail(1, $attach_id);
        wp_reset_postdata();
    }


    public function after_import_setup($selected_import) {
        if (isset($this->config[$selected_import['slug']])) {
            $this->homepage = osf_get_page_by_title($selected_import['import_file_name']);
            $setup          = $this->config[$selected_import['slug']];
            // REVSLIDER
            if ($sliders = $setup['rev_sliders']) {
                if (class_exists('RevSliderAdmin')) {
                    if (!file_exists($this->path_rev)) {
                        wp_mkdir_p($this->path_rev);
                    }
                    foreach ($sliders as $slider) {
                        $this->add_revslider($slider);
                    }
                }
            }

            // Setup Customizer
            remove_theme_mod('osf_theme_custom_style');
            $thememods = $this->get_remote_json($selected_import['customizer'], true);
            foreach ($thememods as $mod => $value) {
                set_theme_mod($mod, $value);
            }

            $this->setup_home_page($selected_import['slug']);

            $this->settings  = $this->get_remote_json($setup['settings'], true);
            $this->templates = $this->settings['templates'];

            // Setup Home page
            update_option('page_on_front', (($this->homepage instanceof WP_Post) ? $this->homepage->ID : 0));
        }
        set_theme_mod('osf_dev_mode', false);
    }

    private function add_revslider($slider) {
        $dest_rev = $this->path_rev . basename($slider);
        if (!file_exists($dest_rev)) {
            file_put_contents($dest_rev, wp_remote_fopen($slider));
            $_FILES['import_file']['error']    = UPLOAD_ERR_OK;
            $_FILES['import_file']['tmp_name'] = $dest_rev;

            $revslider = new RevSlider();
            $revslider->importSliderFromPost(true, 'none');
        }
    }

    /**
     * @param $link
     *
     * @return object|boolean
     */
    private function get_remote_json($link, $assoc = false) {
        $content = wp_remote_get($link);
        if ($content instanceof WP_Error) {
            return false;
        }

        $arr = json_decode($content['body'], $assoc);

        return $arr;

    }

    public function reset_theme_mods() {
        $mods = json_decode(file_get_contents(trailingslashit(XSTREET_CORE_PLUGIN_DIR) . 'reset-theme-mods.json'));
        foreach ($mods as $mod) {
            remove_theme_mod($mod);
        }
    }

    private function update_nav_menu_item() {
        $params = array(
            'posts_per_page' => -1,
            'post_type'      => [
                'nav_menu_item',
            ],
        );
        $query  = new WP_Query($params);
        while ($query->have_posts()): $query->the_post();
            wp_update_post(array(
                // Update the `nav_menu_item` Post Title
                'ID'         => get_the_ID(),
                'post_title' => get_the_title()
            ));
        endwhile;

    }

    private function fixelementor() {
        $datas = $this->get_data_elementor();
        $query = new WP_Query(array(
            'post_type'      => [
                'page',
                'post',
                'elementor_library',
                'header',
                'footer',
                'opal_menu_item'
            ],
            'posts_per_page' => -1
        ));
        while ($query->have_posts()): $query->the_post();
            global $post;
            $postid = get_the_ID();
            if (get_post_meta($post->ID, '_elementor_edit_mode', true) === 'builder') {
                $data = json_decode(get_post_meta($postid, '_elementor_data', true));
                if (!boolval($data)) {
                    if (isset($datas[$post->post_name])) {
                        update_post_meta($postid, '_elementor_data', wp_slash(wp_json_encode($datas[$post->post_name])));
                    }
                }
            }
        endwhile;
        wp_reset_postdata();
    }

    private function setup_home_page($slug) {
        if ($slug == '4') {
            set_theme_mod('osf_woocommerce_product_style', '2');
            set_theme_mod('osf_layout_general_layout_mode', 'boxed');
            set_theme_mod('osf_layout_general_layout_boxed_width', '1600');
            set_theme_mod('osf_layout_general_layout_boxed_offset', '0');
            set_theme_mod('background_color', '000');
        } else {
            set_theme_mod('osf_layout_general_layout_mode', 'wide');
            set_theme_mod('osf_layout_general_layout_boxed_width', '1170');
            set_theme_mod('osf_layout_general_layout_boxed_offset', '30');
            set_theme_mod('background_color', 'f8f8f8');
        }
    }

